pragma solidity ^0.5;

import "./Cloner.sol";
import "./ETHCallOptionToken.sol";
import "./ETHPutOptionToken.sol";
import "./ProxyInterface.sol";


contract OptionTokenRegistry {
    mapping(uint128 => mapping(uint128 => address)) public calls;
    mapping(uint128 => mapping(uint128 => address)) public puts;

    event OptionTokenRegistered(address token, bool isCall, uint128 expiration, uint128 strike);

    function register(uint128 expiration, uint128 strike) external {
        bytes memory code = getCode(msg.sender);
        bytes32 signature = keccak256(code);
        // bytes32 OPTION_TOKEN_SIGNATURE =
        // require(signature == )
    }

    function getCode(address _addr) private view returns (bytes memory o_code) {
        assembly {
            // retrieve the size of the code, this needs assembly
            let size := extcodesize(_addr)
            // allocate output byte array - this could also be done without assembly
            // by using o_code = new bytes(size)
            o_code := mload(0x40)
            // new "memory end" including padding
            mstore(0x40, add(o_code, and(add(add(size, 0x20), 0x1f), not(0x1f))))
            // store length in memory
            mstore(o_code, size)
            // actually retrieve the code, this needs assembly
            extcodecopy(_addr, add(o_code, 0x20), 0, size)
        }
    }
}

